script:
frappe.ui.form.on("Employee", {
	refresh(frm) {
		let duration = moment.duration(moment().diff(cur_frm.doc.date_of_birth));
        const formatDuration = (duration) => {
            let years = duration.years();
            let months= duration.months();
            let days= duration.days();
            let result = '';
            if (years === 1) {
                result += '1 year ';
            } else if (years > 1) {
                result += years + ' years ';
            }
            if (months === 1) {
                result += '1 month ';
            } else if (months > 1) {
                result += months + ' months ';
            }
            if (days === 1) {
                result += '1 day ';
            } else if (days > 1) {
                result += days + ' days ';
            }
            return result;
        }

        cur_frm.set_value("age", formatDuration(duration));
        cur_frm.add_fetch('reports_to',  'employee_name',  'reports_to_name');

	}

})

frappe.listview_settings['Leave Application'] = frappe.listview_settings['Leave Application'] || {};
frappe.listview_settings['Leave Application'] = {
    // add fields to fetch
    add_fields: ['employee_name', 'leave_type', 'from_date', 'to_date', 'status'],

    hide_name_column: true, // hide the last column which shows the `name`

    // add a custom button for each row
    button: {
        show(doc) {
            return doc.docstatus == 1;
        },
        get_label() {
            return 'View';
        },
        get_description(doc) {
            //return __('View {0}', [`${doc.name}`])
            return __('View Details')
        },
        action(doc) {
            frappe.set_route('Form', 'Leave Application', doc.name);
        }
    }
}

cur_frm.add_fetch('employee',  'branch',  'branch');
frappe.ui.form.on("Leave Application", {
	make_dashboard: function(frm) {
		var leave_details;
		let lwps;
		if ( frm.doc.employee ) {
			frappe.call({
				method: "hrms.hr.doctype.leave_application.leave_application.get_leave_details",
				async: false,
				args: {
					employee: frm.doc.employee,
					date: frm.doc.from_date || frm.doc.posting_date
				},
				callback: function(r) {
					if (!r.exc && r.message['leave_allocation']) {
						leave_details = r.message['leave_allocation'];
					}
					if (!r.exc && r.message['leave_approver']) {
						frm.set_value('leave_approver', r.message['leave_approver']);
					}
					lwps = r.message["lwps"];
				}
			});
			$("div").remove(".form-dashboard-section.custom");
			frm.dashboard.add_section(
				frappe.render_template('leave_application_dashboard', {
					data: leave_details
				}),
				__("Allocated Leaves")
			);
			frm.dashboard.show();
			let allowed_leave_types = Object.keys(leave_details);

			// lwps should be allowed, lwps don't have any allocation
			allowed_leave_types = allowed_leave_types.concat(lwps);

			frm.set_query('leave_type', function() {
				return {
					filters: [
						['leave_type_name', 'in', allowed_leave_types]
					]
				};
			});
		}
	}
});

frappe.ui.form.on("Leave Application", "on_submit", function(frm, cdt, cdn) {
	var message = "";
	if (frm.doc.workflow_state == "Leave REJECTED") {
		message = 'Your leave application for ' + frm.doc.total_leave_days + ' days has been REJECTED';
	} else if (frm.doc.workflow_state == "Leave APPROVED") {
		message = 'Your leave application for ' + frm.doc.total_leave_days + ' days has been APPROVED';
	}
	frappe.call({
		method: "frappe.core.doctype.sms_settings.sms_settings.send_sms",
		args: {
			receiver_list: [frm.doc.cell_number],
			msg: message,
		},
		callback: function(r) {
			if (r.exc) {
				msgprint(r.exc);
				return;
			}
		}
	});
})
