script:
frappe.call({
    method: "frappe.client.get",
        args: {
            doctype: "User",
            name: frappe.session.user,
             },
             callback(r) {
                 if(r.message) {
                    root_element.querySelector("#full_name").textContent=r.message.full_name;
                    root_element.querySelector('#email').textContent=r.message.email;
                    if(r.message.user_image == ""){
                        root_element.querySelector('#user_image').src="/files/profile_placeholder.png";
                    } else {
                        root_element.querySelector('#user_image').src=r.message.user_image;
                    }

                 }
             }
});

