script:
frappe.call({
    method: "erpnext15_custom.my_scripts.upcoming_holidays.get_upcoming_holidays",
    args: {
        holiday_list_name: "Weekends & Public Holidays 2024",
        limit: 3
    },
    callback: function (r) {
        const upcoming_holidays = r.message;
        let currentHolidayIndex = 0;

        const main = root_element.querySelector('.holiday-container');
        const holidayInfo = main.querySelector('#holidayInfo');
        const prevBtn = main.querySelector('#prevBtn');
        const nextBtn = main.querySelector('#nextBtn');

        function displayCurrentHoliday(action) {
            const currentHoliday = upcoming_holidays[currentHolidayIndex];
            const formattedDate = formatDate(currentHoliday.holiday_date);

            // Apply the fading effect only if the action is 'prev' or 'next'
            if (action === 'prev' || action === 'next') {
                // Apply the fading effect
                holidayInfo.style.transition = 'opacity 0.5s';
                holidayInfo.style.opacity = 0;

                setTimeout(() => {
                    holidayInfo.style.opacity = 1;
                    holidayInfo.innerHTML = `
                        <p class="holiday-name">${currentHoliday.description}</p>
                    `;
                    holidayInfo.innerHTML += `<p class="holiday-description">${formattedDate}</p>`;
                }, 500); // Adjust the timeout value based on your preference
            } else {
                // If no fading is required (initial load), set the content directly
                holidayInfo.style.transition = 'none'; // Disable transition for the initial load
                holidayInfo.innerHTML = `
                    <p class="holiday-name">${currentHoliday.description}</p>
                `;
                holidayInfo.innerHTML += `<p class="holiday-description">${formattedDate}</p>`;
                setTimeout(() => {
                    // Re-enable transition after a short delay to prevent immediate fade-in
                    holidayInfo.style.transition = 'opacity 0.5s';
                }, 100);
            }
        }





        prevBtn.addEventListener('click', () => {
            currentHolidayIndex = (currentHolidayIndex - 1 + upcoming_holidays.length) % upcoming_holidays.length;
            displayCurrentHoliday('prev');
        });

        nextBtn.addEventListener('click', () => {
            currentHolidayIndex = (currentHolidayIndex + 1) % upcoming_holidays.length;
            displayCurrentHoliday('next');
        });

        // Initial display
        displayCurrentHoliday();
    }
});

function formatDate(dateString) {
    const date = new Date(dateString);
    const day = date.getDate();
    const month = date.toLocaleString('default', { month: 'long' });
    const year = date.getFullYear();
    const formattedDate = `${addOrdinalSuffix(day)} ${month} ${year}`;
    return formattedDate;
}

function addOrdinalSuffix(day) {
    if (day >= 11 && day <= 13) {
        return `${day}th`;
    }
    switch (day % 10) {
        case 1:
            return `${day}st`;
        case 2:
            return `${day}nd`;
        case 3:
            return `${day}rd`;
        default:
            return `${day}th`;
    }
}

